import streamlit as st
st.title("My Simple Streamlit Webpage")
st.header("Welcome to My Webpage")
st.write("This is a simple webpage created using Streamlit. You can use it to create interactive web apps with Python.")
st.subheader("Contact Form")
name = st.text_input("Name")
email = st.text_input("Email")
message = st.text_area("Message")
if st.button("Submit"):
    st.write(f"Thank you, {name}! Your message has been received.")
st.subheader("Check out this cool image!")
st.image("data:image/jpeg;base64,", caption="An example image")
st.sidebar.title("Sidebar")
st.sidebar.write("This is a sidebar where you can add extra options or information.")
